mapper.select:
SELECT
*
FROM
disinfection_files
WHERE
1=1

    AND factory_num = #{factoryNum}


    AND disinfect_time BETWEEN #{disinfectTimeStart} AND #{disinfectTimeEnd}


    AND disinfect_name = #{disinfectName}


    AND disinfect_quality = #{disinfectQuality}


    AND disinfect_way = #{disinfectWay}


    AND operator = #{operator}


    AND professor = #{professor}


    AND supervisor = #{supervisor}


    AND remark = #{remark}


    AND is_pass1 = #{isPass1}


    AND unpass_reason1 = #{unpassReason1}


    AND is_pass2 = #{isPass2}


    AND unpass_reason2 = #{unpassReason2}

mapper.update:
UPDATE genealogical_files
SET  professor = #{professor},
is_pass1 = #{isPass1},
unpass_reason1 = #{unpassReason1},
gmt_professor = #{gmtProfessor}
WHERE id = #{id}

UPDATE genealogical_files
SET  supervisor = #{supervisor},
is_pass2 = #{isPass2},
unpass_reason2 = #{unpassReason2},
gmt_supervise = #{gmtSupervise}
WHERE id = #{id}
