mapper.select:
SELECT
*
FROM
immune_plan
WHERE
1=1

    AND factory_num = #{factoryNum}


    AND crowd_num = #{crowdNum}


    AND immune_eartag LIKE concat(#{immuneEartagStart},'%')


    AND immune_eartag LIKE concat('%',#{immuneEartagEnd})



    AND immune_time BETWEEN #{immuneTimeStart} AND #{immuneTimeEnd}


    AND immune_kind = #{immuneKind}


    AND immune_way = #{immuneWay}


    AND immune_quality = #{immuneQuality}


    AND immune_during = #{immuneDuring}


    AND operator = #{operator}


    AND professor = #{professor}


    AND supervisor = #{supervisor}


    AND remark = #{remark}


    AND is_pass1 = #{isPass1}


    AND unpass_reason1 = #{unpassReason1}


    AND is_pass2 = #{isPass2}


    AND unpass_reason2 = #{unpassReason2}

mapper.update:
UPDATE immune_plan
SET  professor = #{professor},
     is_pass1 = #{isPass1},
     unpass_reason1 = #{unpassReason1},
     gmt_professor = #{gmtProfessor}
WHERE id = #{id}

UPDATE immune_plan
SET  supervisor = #{supervisor},
is_pass2 = #{isPass2},
unpass_reason2 = #{unpassReason2},
gmt_supervise = #{gmtSupervise}
WHERE id = #{id}
